import 'bootstrap/dist/css/bootstrap.min.css';
import "./DiningHall.css";
import { Card } from 'react-bootstrap';
import MenuPage from '../MenuPage/MenuPage.js';
import { useState } from 'react';

function DiningHall(props) {
  const [pageState, setPageState] = useState(0);

  const InfoCard = ({ location }) => {
    return (
      <Card onClick={() => {setPageState(1)}}>
        <Card.Img variant="top" src="/penn_state_logo.png"/>
        <Card.Body>
          <Card.Title>
            {location}
          </Card.Title>
        </Card.Body>
      </Card>
    )
  }

  return (
    <>
      { 
        pageState === 0 
        ?
        <>
          <p className="fs-1 title">Where we are going?</p>
          {props.locations.map((name) => (
            <InfoCard key={name} location={name} />
          ))}
        </>
        : 
        <>

        </>
      }
    </>
  );
}

export default DiningHall;
