import { Card } from 'react-bootstrap';
import "./Home.css";
import { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function Home() {
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    fetch('/data/getMenu')
      .then((res) => res.json())
      .then((data) => {
        const locs = data.map((el) => {
          return el.Location
        })
        setLocations(locs)
      });
  }, [])

  function infoCard({location, imageSrc}) {
    return (
      <Card style={{ width: '18rem' }}>
        <Card.Img variant="top" src={imageSrc} />
        <Card.Body>
          <Card.Text className="text-end fs-1">
            {location}
          </Card.Text>
        </Card.Body>
      </Card>
    )
  }

  return (
    <div className="mobile-width-section">
      {locations.map((name) => (
        <infoCard location={name} imageSrc="holder.js/100px180" />
      ))}
    </div>
  );
}

export default Home;
