import DiningHall from "./DiningHall/DiningHall.js";
import "./App.css";
import { useEffect, useState } from 'react';
import { ProgressBar } from "react-bootstrap";

function App() {
    const [locations, setLocations] = useState([]);
    const [loadingProgress, setLoadingProgress] = useState(0)

    useEffect(() => {
        setLoadingProgress(0)
        const loading = setInterval(() => {
            if (loadingProgress >= 50) {
                clearInterval(loading)
            }
            setLoadingProgress(p => p + 1)
        }, 50)

        fetch('/data/getMenu')
            .then((res) => res.json())
            .then((data) => {
                const locs = data.map((el) => {
                    return el.Location
                }).sort()
                setLocations(locs)
                setLoadingProgress(100)
            });
    }, [])

    return(
        <div className="mobile-box">
            {
                locations.length == 0 ? <ProgressBar className="progress-bar" now={loadingProgress}/> : <DiningHall locations={locations}/>
            }
        </div>
    )
}

export default App;
